var engine ;
 
define(['./node_modules/endtable/lib','./variables/endtableVariables'], function (endtable,endtableVariables) {
	
    if(engine == null){
	    engine = new endtable.Engine({
			database: endtableVariables.database,
			host: endtableVariables.host,
			user: endtableVariables.user,
			password: endtableVariables.password,
			errorCallback: function(error) {
				// When views aren't found they raise a warning.
				console.log(JSON.stringify(error));
			}
		});
	}
	
    return engine;

});



