define(['app','./routes/admin/level/levels'], function (app,adminLevels) {
    
	
	// ############################################# DEFINE RESQUESTS FOR ADMIN SIDE  #############################################
	
	// Levels call and manage
	app.post("/admin/:id/level",adminLevels.addLevel);
	app.get('/admin/:id/level/:id',adminLevels.selectLevel);
	app.put('/admin/:id/level/:id',adminLevels.updateLevel);
	app.delete('/admin/:id/level/:id',adminLevels.deleteLevel);
	app.get('/admin/:id/levels',adminLevels.selectAllLevels);
	
	// ############################################# DEFINE RESQUESTS FOR CLIENT SIDE #############################################
		
	return app;
});